animation.scss:
// Invi 
.animation-element.invi{
  opacity: 0;
}

// FACE IN
.animation-element.fade-in {
  opacity: 0;
  transition: all 1000ms linear;
}

.animation-element.fade-in.in-view {
  opacity: 1;
}



// SLIDE LEFT
.animation-element.slide-left {
  opacity: 0;
  /*-moz-transition: all 1000ms linear;
  -webkit-transition: all 1000ms linear;
  -o-transition: all 1000ms linear;*/
  transition: all 1000ms linear;
  
 /* -moz-transform: translate3d(-100px, 0px, 0px);
  -webkit-transform: translate3d(-100px, 0px, 0px);
  -o-transform: translate(-100px, 0px);
  -ms-transform: translate(-100px, 0px);*/
  transform: translate3d(-100px, 0px, 0px);
}

.animation-element.slide-left.in-view {
  opacity: 1;
  /*-moz-transform: translate3d(0px, 0px, 0px);
  -webkit-transform: translate3d(0px, 0px, 0px);
  -o-transform: translate(0px, 0px);
  -ms-transform: translate(0px, 0px);*/
  transform: translate3d(0px, 0px, 0px);
}


// SLIDE LEFT LONG
.animation-element.slide-left-long {
  opacity: 0;
  transition: all 1000ms linear;
  transform: translate3d(-300px, 0px, 0px);
}

.animation-element.slide-left-long.in-view {
  opacity: 1;
  transform: translate3d(0px, 0px, 0px);
}

// SLIDE RIGHT
.animation-element.slide-right {
  opacity: 0;
  transition: all 1000ms linear;
  transform: translate3d(100px, 0px, 0px);
}

.animation-element.slide-right.in-view {
  opacity: 1;
  transform: translate3d(0px, 0px, 0px);
}


// SLIDE UP
.animation-element.slide-up {
  opacity: 0;
  transition: all 1000ms linear;
  transform: translate3d(0px, 100px, 0px);
}

.animation-element.slide-up.in-view {
  opacity: 1;
  transform: translate3d(0px, 0px, 0px);
}


// SLIDE UP LONG
.animation-element.slide-up-long {
  opacity: 0;
  transition: all 1000ms linear;
  transform: translate3d(0px, 300px, 0px);
}

.animation-element.slide-up-long.in-view {
  opacity: 1;
  transform: translate3d(0px, 0px, 0px);
}



// SLIDE DOWN
.animation-element.slide-down {
  opacity: 0;
  transition: all 1000ms linear;
  transform: translate3d(0px, -100px, 0px);
}

.animation-element.slide-down.in-view {
  opacity: 1;
  transform: translate3d(0px, 0px, 0px);
}



// Spring 
@keyframes spring { 
  0% {
    transform: scale(1) translateY(0); 
  }
  10% {
    transform: scale(1.2, 0.6); 
  }
  30% { 
    transform: scale(0.8, 1.1) translateY(-50px); 
  }
  50% { 
    transform: scale(1) translateY(0); 
  }
  100% { 
    transform: translateY(0); 
  }
}
.animation-element.spring.in-view {
  animation: spring 5s;
  animation-timing-function: ease;
  animation-iteration-count: infinite;
    opacity: 1;
}


// Shake
.animation-element.shake.in-view {
	  opacity: 1;
  animation: shake 0.5s;
  animation-iteration-count: 2;
}

@keyframes shake {
  0% { transform: translate(1px, 1px) rotate(0deg); }
  10% { transform: translate(-1px, -2px) rotate(-1deg); }
  20% { transform: translate(-3px, 0px) rotate(1deg); }
  30% { transform: translate(3px, 2px) rotate(0deg); }
  40% { transform: translate(1px, -1px) rotate(1deg); }
  50% { transform: translate(-1px, 2px) rotate(-1deg); }
  60% { transform: translate(-3px, 1px) rotate(0deg); }
  70% { transform: translate(3px, 1px) rotate(-1deg); }
  80% { transform: translate(-1px, -1px) rotate(1deg); }
  90% { transform: translate(1px, 2px) rotate(0deg); }
  100% { transform: translate(1px, -2px) rotate(-1deg); }
}
 
 
 // Spin 
 
 .animation-element.spin.in-view {
   opacity: 1;
  animation: spin 0.5s;
  animation-iteration-count: 3;
  animation-timing-function: linear; 
}

 
@keyframes spin {
    from {
        transform:rotate(0deg);
    }
    to {
        transform:rotate(360deg);
    }
}
 
 
 
 // Bounce 
 
 
 .animation-element.bounce.in-view {
	  opacity: 1;
    animation: bounce 0.5s; 
    animation-direction: alternate; 
    animation-timing-function: cubic-bezier(.5, 0.05, 1, .5); 
    animation-iteration-count: 3; 
} 
 
 @keyframes bounce {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-50px);
  }
}
 
 
 // Bounce In
 
 .animation-element.bounce-in.in-view {
	  opacity: 1;
	animation-name: bounceIn;
	animation-duration: 450ms;
	animation-timing-function: linear;
	animation-fill-mode: forwards;
}

@keyframes bounceIn{
  0%{
    opacity: 0;
    transform: scale(0.3) translate3d(0,0,0);
  }
  50%{
    opacity: 0.9;
    transform: scale(1.1);
  }
  80%{
    opacity: 1;
    transform: scale(0.89);
  }
  100%{
    opacity: 1;
    transform: scale(1) translate3d(0,0,0);
  }
}

 .animation-element.wiggle.in-view {
	  opacity: 1;
  animation: wiggle 0.5s;
  animation-iteration-count: 5;
  animation-timing-function: linear; 
}

 @keyframes wiggle {
    0% { transform: rotate(0deg); }
   80% { transform: rotate(0deg); }
   85% { transform: rotate(10deg); }
   95% { transform: rotate(-10deg); }
  100% { transform: rotate(0deg); }
}
 
 
 
 

