	background: #d12229;
	border-radius: 5px;
	color: #fff;
	font-weight: 500;
}

.express-seemore:hover {
	text-decoration: none;
	font-size: 24px;
	padding: 10px 20px;
	background: #d12229;
	border-radius: 5px;
	color: #fff;
	font-weight: 500;
}

.page-sidebar {
	margin-top: 5%;
}

.category-heading {
	border-top: 1px solid #cccccc;
	border-bottom: 1px solid #cccccc;
	display: flex;
	align-items: center;
	justify-content: space-between;
}

.category-heading>.page-heading {
	margin-bottom: 0;
}

#SideCategoryShopByRebateIDs {
