components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<ul class="navUser-section">
    <li class="navUser-item">
        <a class="navUser-action navUser-action--currencySelector has-dropdown" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">{{lang 'common.currency' code=currency_selector.active_currency_code}} <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i></a>
        <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each currency_selector.currencies}}
            <li class="dropdown-menu-item">
                <a href="{{{switch_url}}}">
                    {{#if is_active}}
                    <strong>{{name}}</strong>
                    {{else}}
                    {{name}}
                    {{/if}}
                </a>
            </li>
            {{/each}}
        </ul>
    </li>
</ul>
{{/if}}

components/common/navigation-list-altern:
{{#if children}}
    <a class="navPages-action navPages-action-depth-max has-subMenu is-root{{#if is_active}} activePage{{/if}}" href="{{url}}" data-collapsible="navPages-{{id}}">
        {{name}} <i class="icon navPages-action-moreIcon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i>
    </a>
    {{> components/common/navigation-dropdown}}
{{else}}
    <a class="navPages-action{{#if is_active}} activePage{{/if}}" href="{{url}}">{{name}}</a>
{{/if}}

components/common/navigation-list-new:
{{#if children}}
    <a class="navPages-action has-subMenu{{#if is_active}} activePage{{/if}}" href="{{url}}"><img class="nav-image" src="{{cdn (concat 'img/homepage/' (concat name '.png'))}}"  />
        <span style="white-space: nowrap;">{{name}}</span>
    </a>
    <div class="navPage-subMenu" id="navPages-{{id}}" aria-hidden="true" tabindex="-1">
    <div class="navChild_name">
        <h2>{{name}}</h2><a class="navPage-subMenu-action navPages-action" href="{{url}}">SHOP ALL  <img src="https://cdn11.bigcommerce.com/s-bek4r4rsz5/product_images/uploaded_images/chevron-right-solid-red-.png"></a>
    </div> 
        <ul class="nav-children-wImage">
            {{#each children}}
                <li>
                    <img class="imageChildNav" src="{{cdn (concat 'img/nav/' (concat  (lowercase (dashcase name)) '.png'))}}"  />
                        <a class="navPage-subMenu-action navPages-action{{#if is_active}} activePage{{/if}}" href="{{url}}">
                            {{name}}
                            <img src="https://cdn11.bigcommerce.com/s-bek4r4rsz5/product_images/uploaded_images/chevron-right-solid-red-.png">
                        </a>
                        <ul class="navPage-childList" id="navPages-{{id}}" >
                            {{#each children}}
                                    {{#all (if name '!==' 'Freestanding Smoothtop Electric Range') (if name '!==' '30" Freestanding Coil Electric Range') (if name '!==' 'Slide In Electric Range') (if name '!==' 'Ice Makers') (if name '!==' 'Slide In Gas Range') (if name '!==' 'Upright Freezers') (if name '!==' 'Chest Freezers') (if name '!==' '30" Free Standing Gas Range') (if name '!==' '20" Free Standing Gas Range') (if name '!==' 'Accessories') (if name '!==' 'Built In Microwave') (if name '!==' 'Countertop Microwave') (if name '!==' 'Countertop Microwaves') (if name '!==' 'Over The Range Microwave') (if name '!==' 'Convertible Hoods') (if name '!==' 'Ducted Hood') (if name '!==' 'Ducted Hoods') (if name '!==' 'Ductless Hoods') (if name '!==' 'Professional Hood') }} 
                                    <li class="navPage-childList-item">
                                        <a class="navPage-childList-action navPages-action{{#if is_active}} activePage{{/if}}" href="{{url}}">{{name}}</a>
                                    </li>
                                {{/all}}
                            {{/each}}
                        </ul>
                </li>
            {{/each}}
        </ul> 
    </div>
{{else}}
    {{#if name '==' 'Delivery Express'}}
        <a href="/delivery-express/" class="navPages-action has-subMenu{{#if is_active}} activePage{{/if}}"><img class="nav-image" src="{{cdn 'assets/img/homepage/delivery-express.png'}}"  /><span style="white-space: nowrap;">{{name}}</span></a>
    {{/if}}
{{/if}}

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                    <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                </a>
            </li>
        {{/each}}
    </ul>
{{/if}}
